import CourseGoal from "./components/CourseGoal";
import Header from "./components/Header";
import GoalImage from "./assets/goals.jpg";
import "./App.css";
import { useState } from "react";

type CourseGoals = {
  id: number;
  title: string;
  description: string;
};

function App() {
  const [goals, setGoal] = useState<CourseGoals[]>([]);

  const addGoalHandler = () => {
    const newGoal: CourseGoals = {
      id: Math.random(),
      title: "React + TypeScript",
      description: "Learn it thoroughly",
    };
    setGoal((prevGoals) => {
      return [...prevGoals, newGoal];
    });
  };
  return (
    <main>
      <div>
        <Header image={{ src: GoalImage, alt: "Goal Image" }}>
          Your Course Goals
        </Header>
        <button onClick={addGoalHandler}>Add Goal</button>
        <ul>
          {goals.map((goal) => (
            <li key={goal.id}>
              {<CourseGoal title={goal.title} description={goal.description} />}
            </li>
          ))}
        </ul>
      </div>
    </main>
  );
}

export default App;
