import { useRef, type FormEvent } from "react";

type AddGoal = {
  onAdd: (goal: string, summary: string) => void;
};

const NewGoal = ({ onAdd }: AddGoal) => {
  const goal = useRef<HTMLInputElement>(null);
  const summary = useRef<HTMLInputElement>(null);

  const submitHandler = (event: FormEvent<HTMLFormElement>) => {
    event?.preventDefault();

    const enteredGoal = goal.current!.value;
    const enteredSummary = goal.current!.value;

    if (enteredGoal.trim().length !== 0 && enteredSummary.trim().length !== 0) {
      onAdd(enteredGoal, enteredSummary);
    } else {
      const errorMessage = <p style={{ color: "red" }}>Please provide value</p>;
    }

    goal.current && (goal.current.value = "");
    summary.current && (summary.current.value = "");
  };
  return (
    <form onSubmit={submitHandler}>
      <p>
        <label htmlFor="goal">Your Goal</label>
        <input id="goal" type="text" ref={goal} />
      </p>
      <p>
        <label htmlFor="goal">Summary</label>
        <input id="summary" type="text" ref={summary} />
      </p>
      <p>
        <button>Add Goal</button>
      </p>
    </form>
  );
};

export default NewGoal;
