import CourseGoal from "./components/CourseGoal";
import Header from "./components/Header";
import GoalImage from "./assets/goals.jpg";
import "./App.css";
import { useState } from "react";

type CourseGoal = {
  id: number;
  title: string;
  description: string;
};

function App() {
  const [goals, setGoal] = useState<CourseGoal[]>([]);

  const addGoalHandler = () => {
    const newGoal: CourseGoal = {
      id: Math.random(),
      title: "React + TypeScript",
      description: "Learn it thoroughly",
    };
    setGoal((prevGoals) => {
      [...prevGoals, newGoal];
    });
  };
  return (
    <div>
      <Header image={{ src: GoalImage, alt: "Goal Image" }}>
        Your Course Goals
      </Header>
      <button onClick={addGoalHandler}>Add Goal</button>
      <CourseGoal
        title="Learn React + TypeScript"
        description="It is good to learn React with Typescript"
      />
    </div>
  );
}

export default App;
