import { ReactNode } from "react";

type InfoBoxProps = {
  mode: "info" | "warning";
  children: ReactNode;
};

const InfoBox = ({ mode, children }: InfoBoxProps) => {
  if (mode === "info") {
    return (
      <aside className="infobox infobox-hint">
        <p>{children}</p>
      </aside>
    );
  }

  return (
    <aside className="infobox infobox-warning">
      <h2>Warning</h2>
      <p>{children}</p>
    </aside>
  );
};

export default InfoBox;
